sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if ( newValue == '') {
		return;
	}

	var VALUES_LOOKUP = {'hr':{'name':'hr','options': [{'value': 'hr1', 'text': 'Human Resources 1'},{'value': 'hr1', 'text': 'Human Resources 2'}]},'facilities': {'name': 'facilities', 'options': [{'value': 'facilities1', 'text': 'Facilities 1'},	{'value': 'facilities1', 'text': 'Facilities 2'}]},	'legal': {'name': 'legal', 'options': [{'value': 'legal1', 'text': 'Legal 1'},{'value': 'legal2', 'text': 'Legal 2'}]}
	};
	var DEFAULT_OPTS = [{'value': 'other', 'text': 'Other'}];
	var FIELD_NAME = 'u_what_needed'

	var requestType = g_form.getValue('u_request_type');
	var whatneeded = g_form.getValue(FIELD_NAME);

	// Clear all of the choices from the What needed field choice list
	g_form.clearOptions(FIELD_NAME);

	if (typeof(VALUES_LOOKUP[requestType]) == "object")
	{
		for (var i=0; i<VALUES_LOOKUP[requestType].options.length; i++)
		{
			g_form.addOption(FIELD_NAME, VALUES_LOOKUP[requestType].options[i].value, VALUES_LOOKUP[requestType].options[i].text);
		}
	}

	if (DEFAULT_OPTS.length != 0)
	{
		for (var i=0; i<DEFAULT_OPTS.length; i++)
		{
			g_form.addOption(FIELD_NAME, DEFAULT_OPTS[i].value, DEFAULT_OPTS[i].text);
		}
	}


	// If the form is loading and it is not a new record, set the u_what_needed value to the
	// value from the record before it was loaded
	if(isLoading && !g_form.isNewRecord()){
		g_form.setValue('u_what_needed', whatneeded);
	}
}
